
public class Computepay {
	public static void main(String[] args) {
		// TODO Auto-generated method stub
			int a[] = { 1, 2, 3, 4, 5 };
			int q[] = { 10, 20, 30, 40, 50};
			int v = q[1] - a[1];
			int w = q[2] - a[2];
			int x = q[3] - a[3];
			int y = q[4] - a[4];
			int z = q[5] - a[5];
			System.out.print(v + ", ");
			System.out.print(w + ", ");
			System.out.print(x + ", ");
			System.out.print(y + ", ");
			System.out.print(z);
		}
	
}
